mapper.delete:
delete from daily_login_report
where statistics_day = #{statisticsDay} AND platform_id = #{platformId} AND
source_type = #{sourceType}

mapper.select:
SELECT

FROM daily_login_report


        statistics_day >= #{timeStart}


        AND statistics_day <= #{timeEnd}


ORDER BY statistics_day DESC, source_type ASC



    LIMIT #{offset}, #{pageSize12}

SELECT
COUNT(id)
FROM daily_login_report


        statistics_day >= #{timeStart}


        AND statistics_day <= #{timeEnd}
