mapper.select:
SELECT SUM(register_population) AS registerPopulation, SUM(login_population) AS loginPopulation, SUM(recharge_population) AS rechargePopulation , SUM(recharge_count) AS rechargeCount, SUM(recharge_amount) AS rechargeAmount FROM interval_source_report
AND statistics_time > #{startDate}
AND statistics_time <= #{endDate}

select statistics_time, sum(register_population) as register_population, sum(login_population) as loginPopulation, sum(recharge_population) as rechargePopulation, sum(recharge_count) as rechargeCount, sum(recharge_amount) as rechargeAmount from interval_source_report
and statistics_time > #{startDate}
and statistics_time <= #{endDate}
group by statistics_time order by statistics_time desc

select statistics_time as statistics_time, sum(register_population) as register_population_sum, sum(login_population) as login_population_sum, sum(recharge_population) as recharge_population_sum, sum(recharge_count) as recharge_count_sum, sum(recharge_amount) as recharge_amount_sum from interval_source_report
and statistics_time > #{startDate}
and statistics_time <= #{endDate}
and source_type in
#{item}
group by statistics_time

select source_type as sourceType, sum(register_population) as registerPopulation, sum(login_population) as loginPopulation, sum(recharge_population) as rechargePopulation, sum(recharge_count) as rechargeCount, sum(recharge_amount) as rechargeAmount from interval_source_report
and statistics_time > #{startDate}
and statistics_time <= #{endDate}
group by source_type order by source_type asc

select DATE_FORMAT(statistics_time,'%H:%i') as statisticsTime, sum(register_population) as registerPopulation, sum(login_population) as loginPopulation, sum(recharge_population) as rechargePopulation, sum(recharge_count) as rechargeCount, sum(recharge_amount) as rechargeAmount from interval_source_report
and statistics_time > #{startDate}
and statistics_time <= #{endDate}
and source_type = #{deviceType}
group by statistics_time order by statistics_time desc
