mapper.select:
select g.stat_date, g.reg_count, g.recharge_count, g.pay_amount, g.reg_total_count, g.dau, g.wau, g.mau, g.create_time, g.update_time, g.game_id, case when g.channel_id= 'EMPTY' then '' else g.channel_id end as channel_id, p.name game_name, c.chname channel_name from gp_tbl_game_channel_daily g left join gp_tbl_platform_info p on p.id = g.game_id left join channel c on c.chid = g.channel_id where 1=1 and date_format(g.stat_date,'%Y-%m-%d') = #{date} and g.game_id = #{gameId}
order by ${orderField} desc
order by ${orderField} asc
